import React from 'react'
import ReactDOM from 'react-dom/client'
const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];
<script>new Date().toLocaleTimeString()</script>
function App(){
    return <div>
            {/* <h1>Hello React</h1> */}
            <Header/>
            <Pizza/>
            <Pizza/>
            <Pizza/>
            <Footer/>
        
        </div>
}

function Header() {
  return <h1>Fast React Pizza Co.</h1>

}

function Menu() {}

function Footer() {
   //we are using createElement instead of JSX - to test it out
  //  return React.createElement('footer', null, "We're currently open")

  return <h3 src = '<script>new Date().toLocaleTimeString()</script>'>HI</h3>
  
  //back to JSX
  // return <footer>{new Date().toLocaleTimeString() }  We're currently open</footer>
}
////functions can be declarative like above or experssions like below
//const Test = function(){}
////Arrow function expression
// const Test = ()=> {}

//creating a Pizza component
function Pizza(){
    return <div>
            <img src='pizzas/spinaci.jpg' alt="Pizza spinaci"/>
            {/* webpack which is the module bundler for this project
            will always look for the assets in the public folder.
            Hence we need not specify the absolute path for the images
            in the 'src' attribute */}
            <h2>Pizza Spinaci</h2>
            <p>Tomato, mozarella, spinach, and ricotta cheese</p>
        </div>
}
        

const container = document.getElementById('root')
const root = ReactDOM.createRoot(container)
root.render(<React.StrictMode><App/></React.StrictMode>)
