import React from 'react'
import ReactDOM from 'react-dom/client'
import './index.css'
const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];

function App(){
    return <div className='container'>
            {/* <h1>Hello React</h1> */}
            <Header/>
            <Menu/>
            <Footer/>
        
        </div>
}

function Header() {
  // const style = {color : "red" , fontSize : "48px" , textTransform : "uppercase"}
  const style = {}
  // return <h1 style={{color : "red" , fontSize : "48px" , textTransform : "uppercase"}}>Fast React Pizza Co.</h1>
  return(
    <header className='header'>
      <h1 style={style} >Fast React Pizza Co.</h1>

    </header>
      ) 

}

function Menu() {

  return (
  <main className='menu'>
        <h2>Our Menu</h2>
        <Pizza  name='Pizza Spinaci' ingredient = 'Tomato, mozarella, spinach, and ricotta cheese' photoName = 'pizzas/spinaci.jpg'/>
     
  </main>)
}

//creating a Pizza component
function Pizza(){
    return <div>
            <img src='pizzas/spinaci.jpg' alt="Pizza spinaci"/>
          
            <h3>Pizza Spinaci</h3>
            <p>Tomato, mozarella, spinach, and ricotta cheese</p>
        </div>
}

function Footer() {
  const hour = new Date().getHours();
  const openHour = 10;
  const closeHour = 22;
  const isOpen = hour >= hour && hour <= closeHour
  // isOpen ? alert("We're currently open") : alert("Sorry we're closed.")

  // if(hour >= hour && hour <= closeHour) alert("We're currently open");
  // else alert("Sorry we're closed.")
  console.log(hour)



   //we are using createElement instead of JSX - to test it out
  //  return React.createElement('footer', null, "We're currently open")

  
  //back to JSX
  return <footer className='footer'>{new Date().toLocaleTimeString() }  We're currently open</footer>
}
////functions can be declarative like above or experssions like below
//const Test = function(){}
////Arrow function expression
// const Test = ()=> {}

